import React, { Component } from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import RaisedButton from 'material-ui/RaisedButton';
import {Card, CardText, CardMedia, CardTitle} from 'material-ui/Card';

import {lightBlue600,redA400,lightBlueA400,amberA400} from 'material-ui/styles/colors';

import data from './requests/places';
import './App.css';


import Title from "./components/Title";

class App extends Component {

  constructor(props){
    super(props);
  }

  places(){
    return data.places.map(place=>{
      return(
        <Card>
          <CardMedia>
            <img src={process.env.PUBLIC_URL + place.imageUrl}/>
          </CardMedia>
          <CardTitle title={place.title}></CardTitle>
          <CardText>{place.description}</CardText>
        </Card>
      );
    })
  }


  render() {
    return (
      <MuiThemeProvider>
        <div className="Header-background">
          <div style={{"width":"80%","margin":"0 auto"}}>
            <div className="Header-main">
              <Title></Title>
              <RaisedButton label="Crear cuenta" primary={true}/>
              <img className="Header-ilustration" src={process.env.PUBLIC_URL + './images/bg-geo.png'} />
            </div>

            <div>
            <ul>
                <Card className="Header-benefit">
                  <CardText>
                    <div className="row">
                      <div className="Header-benefit-image" style={{'backgroundColor': redA400}}>
                        <img src={process.env.PUBLIC_URL + './images/hearth.png'} />
                      </div>
                      <div className="Header-benefit-content">
                        <h3>Califica con emociones</h3>
                        <p>Califica con experiencias no con numeros</p>
                      </div>
                    </div>
                  </CardText>
                </Card>

                <Card className="Header-benefit">
                  <CardText>
                    <div className="row">
                      <div className="Header-benefit-image" style={{'backgroundColor': lightBlueA400}}>
                        <img src={process.env.PUBLIC_URL + './images/no-internet.png'} /></div>
                      <div className="Header-benefit-content">
                        <h3>¿Sin internet? ¡Sin problema!</h3>
                        <p>Diseñado para funcionar sin internet o conexiones lentas</p>
                      </div>
                    </div>
                  </CardText>
                </Card>

                <Card className="Header-benefit">
                  <CardText>
                    <div className="row">
                      <div className="Header-benefit-image" style={{'backgroundColor': amberA400}}>
                        <img src={process.env.PUBLIC_URL + './images/star.png'} /></div>
                      <div className="Header-benefit-content">
                        <h3>Califica con emociones</h3>
                        <p>Califica con experiencias no con numeros</p>
                      </div>
                    </div>
                  </CardText>
                </Card>
              </ul>
            </div>

          </div>
        </div>

        <div style={{'backgroundColor': lightBlue600, 'padding':'50px'}}>
              {this.places}
        </div>

      </MuiThemeProvider>
    );
  }
}

export default App;
